export declare const WarningStyles: {
    color: string;
    backgroundColor: string;
    '&:focus': {
        boxShadow: string;
    };
};
declare const buttons: {
    primary: {
        color: string;
        backgroundColor: string;
        '&:focus': {
            boxShadow: string;
        };
        '&.warning': {
            color: string;
            backgroundColor: string;
            '&:focus': {
                boxShadow: string;
            };
        };
    };
    secondary: {
        color: string;
        backgroundColor: string;
        '&:focus': {
            boxShadow: string;
        };
        '&.warning': {
            color: string;
            backgroundColor: string;
            '&:focus': {
                boxShadow: string;
            };
        };
    };
    tertiary: {
        color: string;
        backgroundColor: string;
        '&:hover': {
            opacity: number;
            backgroundColor: string;
        };
        '&:focus': {
            boxShadow: string;
        };
        '&.warning': {
            color: string;
            '&:focus': {
                boxShadow: string;
            };
        };
    };
    outline: {
        color: string;
        backgroundColor: string;
        '&:focus': {
            boxShadow: string;
        };
        '&.warning': {
            color: string;
            borderColor: string;
            '&:focus': {
                boxShadow: string;
            };
        };
    };
};
export default buttons;
